import "./styles.css";
import User from './components/user/User';
import Navbar from './components/navbar/Navbar';

import { useState } from 'react';

const App = () => {
  const [ state, setState ] = useState({

    clickCount: 0,
    backColor: 'yellow',
  })

  const colorMapper = () => {
    const hexColor = "#" + Math.floor(Math.random() * 16777215).toString(16);
    return hexColor;
  };

  const clickHandler = () => {
    if (state.clickCount % 5 === 4) {
      setState({
        // ...state,
        backColor: colorMapper(),
        clickCount : state.clickCount + 1
      })
    }
    else {
      setState({
        // ...state, 
        clickCount : state.clickCount + 1 
      });
    }
  };

    return (
      <div className="App">
        <Navbar></Navbar>
        <h1>React - state</h1>
        <p>Count is: {state.clickCount}</p>
        <button onClick={clickHandler}> Click me </button>
        <User
          theColor={state.backColor}
          firstName={state.userA.firstName}
          image={state.userA.avatarUrl}
          bootcampName={state.bootcamp}
           />
        <User 
          firstName={state.userB.firstName}
          image={state.userB.avatarUrl}
          bootcampName={state.bootcamp}
           />
      </div>
    );
}
export default App;
